mapper.select:
select id, exam_id, group_id
from exam_group
where group_id = #{groupId,jdbcType=INTEGER}

mapper.update:
update exam_group
set exam_id = #{examId,jdbcType=INTEGER},
  group_id = #{groupId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, exam_id, group_id
from exam_group
where id = #{id,jdbcType=INTEGER}

select id, exam_id, group_id
from exam_group
where exam_id = #{examId,jdbcType=INTEGER} and group_id=#{groupId}
