mapper.insert:
insert into exam (id, exam_name, paper_code,
begin_time, end_time, duration,status
)
values (#{id,jdbcType=INTEGER}, #{examName,jdbcType=VARCHAR}, #{paperCode,jdbcType=VARCHAR},
#{beginTime,jdbcType=VARCHAR}, #{endTime,jdbcType=VARCHAR}, #{duration,jdbcType=VARCHAR},
#{status,jdbcType=VARCHAR}
)

mapper.update:
update exam set
  status = #{status,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, exam_name, paper_code, begin_time, end_time, duration,status
from exam
where id = #{id,jdbcType=INTEGER}
